import React from "react";

const Home = () => {
  const productList = [
    { name: "Mac Book", price: "1,20,000", imgSrc: "asd", id: "123456789" },
    { name: "Shoes", price: "7,200", imgSrc: "asd", id: "abcdefghij" },
  ];

  return (
    <div className="home">
    { 
     productList.map(i=>(
      <ProductCard key={i.id} imgSrc={i.img} />
     ))
        
    }
    </div>
  );
};

const ProductCard = ({ name, id, price, handler, imgSrc }) => {
  <div className="productCard">
    <img src={imgSrc} alt={name} />
    <p>{name}</p>
    <h4>â‚¹{price}</h4>
    <button onClick={() => handler()}>Add to Cart</button>
  </div>;
};

export default Home;
