import React from "react";
import { toast } from "react-hot-toast";
import { AiFillDelete } from "react-icons/ai";
import { useDispatch, useSelector } from "react-redux";

const Cart = () => {

  const { cartItems,subTotal,tax,shipping,total } = useSelector(state=>state.cart)

  const dispatch = useDispatch();

  const increment=(id)=>{
    dispatch({
      type:"addToCart",
      payload:{id},
    })
    toast.success("Item Incremented")
    dispatch({type:"calculatePrice"})
  }

  const decrement=(id)=>{
    dispatch({
      type:"decrement",
      payload:id,
    })
    toast.success("Item Decremented")
    dispatch({type:"calculatePrice"})
  }

  const deleteHandler=(id)=>{
    dispatch({
      type:"deleteFromCart",
      payload:id,
    })
    toast.success("Item Removed")
    dispatch({type:"calculatePrice"})
  }

  return (
    <div className="cart">
      <main>
{
  cartItems.length > 0 ? (
    cartItems.map(i=>(
      <CartItem
      imgSrc={i.imgSrc}
      name={i.name}
      price={i.price}
      qty={i.quantity}
      id={i.id}
      key={i.id}
      decrement={decrement}
      increment={increment}
      deleteHandler={deleteHandler}
    />
    ))
  ) : (
    <h2 className="noItems">No Items Yet</h2>
  )
}
      </main>

      <aside>
        <h2>SubTotal: ₹{subTotal}</h2>
        <h2>Shipping: ₹{shipping}</h2>
        <h2>Tax: ₹{tax}</h2>
        <h2>Total: ₹{total}</h2>
      </aside>
    </div>
  );
};

const CartItem = ({
  imgSrc,
  name,
  price,
  qty,
  decrement,
  increment,
  deleteHandler,
  id,
}) => (
  <div className="cartItem">
    <img src={imgSrc} alt="Item" />
    <article>
      <h3>{name}</h3>
      <p>₹{price}</p>
    </article>

    <div>
      <button onClick={() => decrement(id)}>-</button>
      <p>{qty}</p>
      <button onClick={() => increment(id)}>+</button>
    </div>

    <AiFillDelete onClick={() => deleteHandler(id)} />
  </div>
);

export default Cart;
